SELECT [combinedVehicleStops]![stop_id] & [combinedVehicleStops]![timestamp] AS [Index], combinedVehicleStops.stop_id, combinedVehicleStops.stop_cause, combinedVehicleStops.service_area, combinedVehicleStops.subject_race, combinedVehicleStops.subject_sex, combinedVehicleStops.subject_age, combinedVehicleStops.timestamp, combinedVehicleStops.stop_date, combinedVehicleStops.stop_time, combinedVehicleStops.sd_resident, combinedVehicleStops.arrested, combinedVehicleStops.searched, combinedVehicleStops.obtained_consent, combinedVehicleStops.contraband_found, combinedVehicleStops.property_seized, combinedVehicleStopsSearchDetails_Flat.search_details_type_flat, combinedVehicleStopsSearchDetails_Flat.search_details_description_flat, IIf(Left([combinedVehicleStops]![stop_cause],1)='&',Mid([combinedVehicleStops]![stop_cause],2,Len([combinedVehicleStops]![stop_cause])-1),[combinedVehicleStops]![stop_cause]) AS stop_cause_clean, IIf(IsNumeric([subject_age]),IIf(CLng([subject_age])<13 Or CLng([subject_age])>100,Null,CLng([subject_age])),Null) AS age_val, IIf(IsError(CDate([stop_date])),Null,CLng(Year(CDate([stop_date])))) AS year_val, IIf(IsDate([stop_date] & ' ' & [stop_time]),CDate([stop_date] & ' ' & [stop_time]),IIf(IsDate([stop_date]),CDate([stop_date]),Null)) AS timestamp_val, [Vehicle_stops_race_codes]![Description] AS race
FROM ((combinedVehicleStops LEFT JOIN Vehicle_stops_race_codes ON combinedVehicleStops.subject_race = Vehicle_stops_race_codes.[Race Code]) LEFT JOIN combinedVehicleStops_DuplicateIDs ON combinedVehicleStops.stop_id = combinedVehicleStops_DuplicateIDs.stop_id) LEFT JOIN combinedVehicleStopsSearchDetails_Flat ON combinedVehicleStops.stop_id = combinedVehicleStopsSearchDetails_Flat.stop_id
WHERE ((([combinedVehicleStops_DuplicateIDs]![stop_id]) Is Null));
